import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Signup = () => {
	const [data, setData] = useState({
		prereg_name: "",
		prereg_email: "",
		prereg_mob: "",
		districtd: "",
	});
	const [error, setError] = useState("");
	const navigate = useNavigate();

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "https://yip.kerala.gov.in/yipapp/index.php/Idea2021/add_pre_reg";
			const { data: res } = await axios.post(url,{
				prereg_name:data.prereg_name,
				prereg_email:data.prereg_email,
				prereg_mob:data.prereg_mob,
				districtd:data.districtd
			});
			// 
			console.log(res.message);
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};

	return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>
					<h1>Welcome Back</h1>
					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Sing in
						</button>
					</Link>
				</div>
				<div className={styles.right}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Create Account</h1>
						<input
							type="text"
							placeholder="Name"
							name="prereg_name"
							onChange={handleChange}
							value={data.prereg_name}
							required
							className={styles.input}
						/>

						<input
							type="email"
							placeholder="Email"
							name="prereg_email"
							onChange={handleChange}
							value={data.prereg_email}
							required
							className={styles.input}
						/>
						<input
							type="text"
							placeholder="Phon No"
							name="prereg_mob"
							onChange={handleChange}
							value={data.prereg_mob}
							required
							className={styles.input}
						/>
						<input
							type="District"
							placeholder="District"
							name="districtd"
							onChange={handleChange}
							value={data.districtd}
							required
							className={styles.input}
						/>
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" className={styles.green_btn}>
							Sing Up
						</button>
					</form>
				</div>
			</div>
		</div>
	);
};

export default Signup;
