import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Signup = () => {
    const [inpt, setinput] = useState({
        prereg_name: "",
        prereg_email: "",
        prereg_mob: "",
        districtd: "",
        // districtd2: "",
    });

    const [error, setError] = useState("");
    const [showOTPDialog, setShowOTPDialog] = useState(false);
    const [otp, setOTP] = useState("");
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setinput((inpt) => ({ ...inpt, [name]: value }));
    };

    const registerData = () => {
        const formData = new FormData();
        formData.append("prereg_name", inpt.prereg_name);
        formData.append("prereg_email", inpt.prereg_email);
        formData.append("prereg_mob", inpt.prereg_mob);
        formData.append("districtd", inpt.districtd);


        axios
            .post("https://yip.kerala.gov.in/yipapp/index.php/Idea2021/add_pre_reg", formData)
            .then(() => {
                setShowOTPDialog(true); // Show OTP verification dialog
            })
            .catch((err) => {
                console.log(err);
                alert("An error occurred during registration. Please try again later.");
            });
    };

    const handleOTPChange = (e) => {
        setOTP(e.target.value);
    };

    const handleOTPSubmit = () => {
        // Send the OTP to the server for verification
        // You can use axios to send a request similar to the registration process
        // Once the OTP is verified, you can proceed with further actions
        // For demonstration purposes, let's assume OTP is valid and registration is complete.
        setShowOTPDialog(false);
        alert("Registration successful!");
        navigate("/login"); // Redirect the user to the login page after successful registration
    };

    const OTPDialog = (
        <div className={styles.otp_dialog}>
            <h2>OTP Verification</h2>
            <p>Please enter the OTP sent to your email/phone number.</p>
            <input type="text" placeholder="Enter OTP" value={otp} onChange={handleOTPChange} />
            <button onClick={handleOTPSubmit}>Submit</button>
        </div>
    );

    return (
        <div className={styles.signup_container}>
            <div className={styles.signup_form_container}>
                <div className={styles.left}>
                    <h1>Welcome Back</h1>
                    <Link to="/login">
                        <button type="button" className={styles.white_btn}>
                            Sign in
                        </button>
                    </Link>
                </div>
                <div className={styles.right}>
                    <form className={styles.form_container}>
                        <h1>Create Account</h1>
                        <input
                            type="text"
                            placeholder="Name"
                            name="prereg_name"
                            onChange={handleChange}
                            value={inpt.prereg_name}
                            required
                            className={styles.input}
                        />
                        <input
                            type="email"
                            placeholder="Email"
                            name="prereg_email"
                            onChange={handleChange}
                            value={inpt.prereg_email}
                            required
                            className={styles.input}
                        />
                        <input
                            type="text"
                            placeholder="Phone No"
                            name="prereg_mob"
                            onChange={handleChange}
                            value={inpt.prereg_mob}
                            required
                            className={styles.input}
                        />
                        <input
                            type="text"
                            placeholder="District"
                            name="districtd"
                            onChange={handleChange}
                            value={inpt.districtd}
                            required
                            className={styles.input}
                        />
                        {/* <input
                            type="text"
                            placeholder="Second District (Optional)"
                            name="districtd2"
                            onChange={handleChange}
                            value={inpt.districtd2}
                            style={{ display: inpt.districtd ? "block" : "none" }}
                            className={styles.input}
                        /> */}
                        {error && <div className={styles.error_msg}>{error}</div>}
                        <button type="button" className={styles.green_btn} onClick={registerData}>
                            Sign Up
                        </button>
                    </form>
                </div>
            </div>
            {showOTPDialog && OTPDialog}
        </div>
    );
};

export default Signup;
