import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Signup = () => {
	const [inpt, setinput] = useState({
		prereg_name: '',
		prereg_email: '',
		prereg_mob: '',
		districtd: ''
	  });
	const [error, setError] = useState("");
	const navigate = useNavigate();

	const handleChange = (e) => {
		const { name, value } = e.target;
		setinput((inpt) => ({ ...inpt, [name]: value }));
		console.log(inpt);
	  };

	  const registerData = () => {
		// Check if any of the fields are empty
		 if (!inpt.prereg_name || !inpt.prereg_email || !inpt.prereg_mob || !inpt.districtd) {
		   alert('Please fill in all the required fields.');
		   return;
		 }
	   // email vallidation
	   const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	   if (!emailRegex.test(inpt.prereg_email)) {
		 alert('Please enter a valid email address.');
		 return;
	   }
	   // mobile vallidation
	   const mobileRegex = /^[6-9]\d{9}$/;
	   if (!mobileRegex.test(inpt.prereg_mob)) {
		 alert('Please enter a valid 10-digit mobile number.');
		 return;
	   }

	   const formData = new FormData();
	   formData.append('prereg_name', inpt.prereg_name);
	   formData.append('prereg_email', inpt.prereg_email);
	   formData.append('prereg_mob', inpt.prereg_mob);
	   formData.append('districtd', inpt.districtd);

	   console.log(formData);

	   axios.post('https://yip.kerala.gov.in/yipapp/index.php/Idea2021/add_pre_reg', formData)
		 .then(() => {
		   alert('Registered Successfully');
		   setOpenDialog(true);
		 })
		 .catch((err) => {
		   console.log(err);
		   alert('An error occurred during registration. Please try again later.');
		   setOpenDialog(true);
		 });
	 };


	return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>
					<h1>Welcome Back</h1>
					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Sing in
						</button>
					</Link>
				</div>
				<div className={styles.right}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Create Account</h1>
						<input
							type="text"
							placeholder="Name"
							name="prereg_name"
							onChange={handleChange}
							value={data.prereg_name}
							required
							className={styles.input}
						/>

						<input
							type="email"
							placeholder="Email"
							name="prereg_email"
							onChange={handleChange}
							value={data.prereg_email}
							required
							className={styles.input}
						/>
						<input
							type="text"
							placeholder="Phon No"
							name="prereg_mob"
							onChange={handleChange}
							value={data.prereg_mob}
							required
							className={styles.input}
						/>
						<input
							type="District"
							placeholder="District"
							name="districtd"
							onChange={handleChange}
							value={data.districtd}
							required
							className={styles.input}
						/>
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" className={styles.green_btn} onC>
							Sing Up
						</button>
					</form>
				</div>
			</div>
		</div>
	);
};

export default Signup;
